    componentDidMount() {
        fetch("https://swapi.co/api/people")
        .then(results => {
            return results.json();
        }). then (data => { 
            let pictures = data.results.map((pic) => {
                return (
                    <div key={pic.results} className="allFilms">
                        <h2>{pic.name}</h2>
                        <p>{pic.birth_year}</p>
                    </div>
                )
            })
            this.setState({pictures: pictures});
      });
    }

