import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from 'react-router-dom';
import { Home } from "./components/Home"
import AddTripButton from "./components/Films"

export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = { 
        isClacked: false
     }
  }



  triggerViewFilms = () => {
    this.setState({
      ...this.state,
      isEmptyState: false,
      isViewAllState: true
    })
  }

  render() {
    console.log(this.state.isClacked);

    return (
      <React.Fragment>
      <Router>
        <div className="nav">
          <Link to="/">Home</Link>
          <Link to="/films">Films</Link>
        </div>
      </Router>
      <Router>
            <Switch>
                <Route path="/films">
                </Route>
            </Switch>
      </Router>
      <AddTripButton addTrip={this.triggerAddTripState} />
      <div>
      {this.state.isEmptyState && <AddTripButton addTrip={this.triggerAddTripState} />}

      {this.state.isAddTripState && <Home />}
    </div>

      </React.Fragment>
    );
  }
}

ReactDOM.render(
  <App/>,
  document.getElementById('root')
);
