import React from "react";

export class Films extends React.Component {
  constructor(props) {
    super(props);
  }

  setDerivedStateFromProps() {
    fetch("https://swapi.co/api/films/")
      .then(res => res.json())
      .then(data => {
        this.setState({ films: data });
      })
      .catch(console.log);
  }

  componentDidMount() {
    console.log(this.state.films);
}

  render() {
    return <p>turn it up</p>;
  }


}
