import React from "react";
import ReactDOM from "react-dom";

export class Films extends React.Component {
  constructor(props) {
    super(props);   
  }

  setDerivedStateFromProps(props, state) {
      console.log(props,state);
  }

  render() {
      console.log(this.state, "hey");
    var test = Object.keys(this.state).map(function (key) {
        return <option key={key} value={key}>{this.state[key]}</option>;
    });

    return <p>List of things is {this.state}</p>;
  }

  componentDidMount() {
    fetch("https://swapi.co/api/films/")
    .then(res => res.json())
    .then(data => {
      this.setState({ films: data });
    })
    .catch(console.log);
  }

  componentWillUnmount() {
  }
}
