import React from "react";
import Loader from "react-loader-spinner";
import uuid from "uuidv4";

export class Films extends React.Component {
  constructor(props) {
    super(props);
    this._isMounted = false;
    this.state = {
      pictures: [],
      busy: this.props.busy,
      loading: true
    };
  }

  fetchSWAPI = () => {
    console.log(this.state.busy, "changed?");
    fetch("https://swapi.co/api/films")
      .then(results => {
        return results.json();
      })
      .then(data => {
        console.log(data);
        this.setState({ busy: false });
        let movies = data.results.map(pic => {
          return (
            <div key={uuid()} className="allFilms">
              <h2 className="nameOfThing">{pic.title}</h2>
              <p>{pic.opening_crawl}</p>
            </div>
          );
        });
        this.setState({
          pictures: movies,
          loading: false
        });
        console.log(movies, "movies");
      })
      .catch(function(error) {
        console.log(error.number + ": " + error.message);
      })
      .finally(function() {
        console.log("finished");
      });
  };

  componentDidMount() {
    this._isMounted = true;
    this.fetchSWAPI();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  render() {
    return (
      <div className="container1 ">
        <Loader
          type="ThreeDots"
          color=""
          height={100}
          width={460}
          offset={100}
          visible={this.state.busy}
          className="loaderCon col-centered spinz"
        />
        <div className="container2">{this.state.pictures}</div>
      </div>
    );
  }

  //console.log("compdidmount");
  /*
            var test = Object.keys(this.state).map(function (key) {
                return <option key={key} value={key}>{this.state[key]}</option>;
            });*/

  //}
}

