import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from 'react-router-dom';
import { Home } from "./components/Home"
import ViewFilms, { Films } from "./components/Films"

export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = { isEmptyState: true }
  }

  triggerViewFilms = () => {
    this.setState({
      ...this.state,
      isEmptyState: false,
      isViewAllState: true
    })
  }

  render() {
    return (
      <React.Fragment>
      <Router>
        <div className="nav">
          <Link to="/">Home</Link>
          <Link to="/films">Films</Link>
        </div>
      </Router>
      <Router>
            <Switch>
                <Route path="/films">
                  <Films />
                </Route>
            </Switch>
      </Router>
      <ViewFilms viewAll={this.triggerViewFilms} />

      <div>
      {this.state.isEmptyState && <AddTripButton addTrip={this.triggerAddTripState} />}

      {this.state.ViewAllState && <AnotherComponent />}
    </div>
      </React.Fragment>
    );
  }
}
