import React from "react";
import { string } from "../../../../Library/Caches/typescript/3.6/node_modules/@types/prop-types";

let x = 0;
export class People extends React.Component {
  constructor(props) {
    super();
    this.state = {
      peoples: []
    };
    /*
      fetch("https://swapi.co/api/films")
      .then(res => res.json())
      .then(data => {
        this.setState({ films: data });
      })
      .catch(console.log);*/
  }

  componentDidMount() {
    for (let i = 1; i < 17; i++) {
      fetch("https://swapi.co/api/people/")
        .then(results => {
          return results.json();
        })
        .then(data => {
          let peoples = data.results.map(pic => {
            return (
              <div key={pic.results} className="allFilms">
                <h2>{pic.name}</h2>
                <p>Height: {pic.height}</p>
                <p>Hair Colour: {pic.blond}</p>
                <p>Eye colour: {pic.eye_color}</p>
	            <p>Gender: {pic.gender}</p>
              </div>
            );
          });
          this.setState({ peoples: peoples});
          console.log("state", this.state);
        });
    }
  }

  render() {
    console.log("this is renderrr.... " + x++, this.state);
    return (
      <div className="container2">
        <div className="container1">{this.state.peoples}</div>
      </div>
    );
  }

  shouldComponentUpdate() {
    return true;
  }
}
