import React from "react";
import Loader from 'react-loader-spinner';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
let c = 0; //counter
let cc = 0;
let pageNum = 1;

export class People extends React.Component {
    constructor(props) {
        super();
        this.state = {
          peoples: [],
          page: 0,
          pagination: []
        };
        /*
          fetch("https://swapi.co/api/films")
          .then(res => res.json())
          .then(data => {
            this.setState({ films: data });
          })
          .catch(console.log);*/
      }

      saySomething(something) {
        console.log(something);
    }

    handlePageNext(page) {
        if(this.page === null)
         this.page = 1 ;
        this.page =+ 1
        pageNum++;
        console.log("pageNum from handle func is: ". pageNum);
    }

    fetchSWAPI = () => {
      fetch("https://swapi.co/api/people/?page="+(pageNum))
      .then(results => {
        return results.json();
      })
      .then(data => {
        console.log(data);
        let next = data.next;
        console.log("next is : ", next)
        //let str = next.slice(13);
        this.setState({ pagination: next});
        let peoples = data.results.map(pic => {
          return (
            <Container key={cc++}>
            <Row>
              <Col md={{ span: 10, offset: 1 }} key={c++} className="allFilms align-self-center">
              <h2>{pic.name}</h2>
              <p>Height: {pic.height}</p>
              <p>Hair Colour: {pic.hair_color}</p>
              <p>Eye colour: {pic.eye_color}</p>
              <p>Gender: {pic.gender}</p>
              </Col>
</Row>
</Container>
          );
        });
        this.setState({ peoples: peoples});
        //console.log("state", this.state);
      })
      .catch(function(error) { console.log(error.number + ": " + error.message); }).finally(function() { console.log("finished") });;
  //}


    }


      componentDidMount() {

          this.fetchSWAPI();

    }


      render() {
        //creates virtual DOM... keep render() pure
        return (
          <div className="container1 ">
              <Loader
                      type="ThreeDots"
                      color=""
                      height={100}
                      width={480}
                      offset={100}
                      timeout={4000}
                      className="loaderCon col-centered spinz"/>
            <div className="container2">
            <a href={"?page=" + (this.state.pagination)} onClick={() => this.handlePageNext(this.state.page)}>Next page</a>
                {this.state.peoples}
          </div>
      </div>

        );
      }
}
