import React from "react";

let x = 0;
export class People extends React.Component {
  constructor(props) {
    super();
    this.state = {
      peoples: []
    };
    /*
      fetch("https://swapi.co/api/films")
      .then(res => res.json())
      .then(data => {
        this.setState({ films: data });
      })
      .catch(console.log);*/
  }

  componentDidMount() {
    for (let i = 1; i < 17; i++) {
      fetch("https://swapi.co/api/films")
        .then(results => {
          return results.json();
        })
        .then(data => {
          let pictures = data.results.map(pic => {
            return (
              <div key={pic.results} className="allFilms">
                <h2>{pic.title}</h2>
                <p>{pic.opening_crawl}</p>
              </div>
            );
          });
          this.setState({ pictures: pictures });
          console.log("state", this.state);
        });
    }
  }

  render() {
    console.log("this is renderrr.... " + x++, this.state);
    return (
      <div className="container2">
        <div className="container1">{this.state.peoples}</div>
      </div>
    );
  }

  shouldComponentUpdate() {
    return true;
  }
}
