import React from 'react';

export class Films extends React.Component {
constructor(props) {
    super(props);
}

    state = {
        films: []
    }

    componentDidMount() {
        fetch('https://swapi.co/api/films/')
        .then(res => res.json())
        .then((data) => {
          this.setState({ films: data })
        })
        .catch(console.log);
    }

    render() {
        console.log(this.state.films);
        return (
            <p>turn it up</p>
        );
    }
}
