import React from "react";
import Loader from "react-loader-spinner";

export default class LoaderComp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      busy: this.props.busy,
    };
  }

  //depreciated life cycle
  UNSAFE_componentWillReceiveProps({busy}) {
    this.setState({...this.state,busy})
  }

  static getDerivedStateFromProps(nextProps, prevState){

    if(nextProps.someValue!==prevState.someValue){
      return { someState: nextProps.someValue};
   }
   else return null;
 }

 componentDidUpdate(prevProps, prevState) {
     console.log("prevprops is: ",prevProps);
     console.log("prevstate is: ",prevstate);
     this.setState({...this.state,busy});
     //this.setState({someState: someValue});
     //this.classMethod();
 }

  render() {
    return (
      <div className="container1 ">
        <Loader
          type="ThreeDots"
          color=""
          height={50}
          width={400}
          offset={200}
          visible={this.state.busy}
          className="loaderCon col-centered spinz"
        />
      </div>
    );
  }
}
