import React from 'react';

export class Films extends React.Component {
    state = {
        films: []
    }

    componentDidMount() {
        fetch('https://swapi.co/api/films/')
        .then(res => res.json())
        .then((data) => {
          this.setState({ contacts: data })
        })
        .catch(console.log)
    }


    doSwapi() {
        Swapi.getPerson(1).then((result) => {
            console.log(result);
        });
    }
    render() {
        return (
            <p>turn it up</p>
        );
    }
}
