import React from "react";
import Loader from "react-loader-spinner";

export default class LoaderComp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      busy: this.props.busy,
      testVal: this.props.testVal
    };
  }

  render() {
      console.log("state of busy in loader is: ",this.state.busy);
      let isVisible;
      if (this.state.busy === 0) {
        isVisible = true;
      }
    return (
      <div className="container1 ">
          <h1>{this.state.testVal}</h1>
        <Loader
          type="ThreeDots"
          color=""
          height={50}
          width={400}
          offset={150}
          visible={isVisible}
          className="loaderCon col-centered spinz"
        />
      </div>
    );
  }
}
