import React from "react";
import Loader from "react-loader-spinner";
import uuid from "uuidv4";

let timingReq = 0;
let time = 4000;
let timey = 9;

export class Films extends React.Component {
  constructor() {
    super();
    this._isMounted = false;
    this.state = {
      pictures: [],
      loading: true
    };
  }

LoadingIndicator = props => {
     const promiseInProgress  = usePromiseTracker();

       return (
            promiseInProgress && 
        <h1>Hey some async call in progress ! </h1>
      );  
     }

  fetchSWAPI = () => {

        fetch("https://swapi.co/api/films", {time:true})
      .then(results => {
       // this.measureASyncFunc();
        return results.json();
      })
      .then(data => {
          console.log("time in then is type: ", typeof(time));
          console.log("timey iss: ", timey);
        let movies = data.results.map(pic => {
          return (
            <div key={uuid()} className="allFilms">
              <h2 className="nameOfThing">{pic.title}</h2>
              <p>{pic.opening_crawl}</p>
            </div>
          );
        });
        this.setState({ 
            pictures: movies,
            loading: false
         });
        console.log(movies, "movies");
      })
      .catch(function(error) {
        console.log(error.number + ": " + error.message);
      })
      .finally(function() {
        console.log("finished");
      });
  };

  componentDidMount() {
    this._isMounted = true;
    this.fetchSWAPI();
    if(timingReq !== 0) {   time = timingReq;   }
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  render() {
    const loading = this.state;
    return (
      <div className="container1 ">
        <Loader
          type="ThreeDots"
          color=""
          height={100}
          width={460}
          offset={100}
          timeout={4000}
          className="loaderCon col-centered spinz"
        /> :<div className="container2">{this.state.pictures}</div>}
      </div> 
    );
  }

  //console.log("compdidmount");
  /*
            var test = Object.keys(this.state).map(function (key) {
                return <option key={key} value={key}>{this.state[key]}</option>;
            });*/

  //}
}

