import React from 'react';

export class Films extends React.Component {
constructor(props) {
    super(props);

    fetch('https://swapi.co/api/films/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ films: data })
    })
    .catch(console.log);

    state = {
        count: 7,
        next: null,
        previous: null,
        results: data
    }
}



