import React from "react";

export class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = { filmsCount: 0 };
  }

  setDerivedStateFromProps() {

  }

  render() {
    console.log(this.state.films);
    return <p>list of films is</p>;
  }

  componentDidMount() {
    fetch("https://swapi.co/api/films/")
      .then(res => res.json())
      .then(data => {
        this.setState({ films: data });
      })
      .catch(console.log);
  }
}
